-- Populate special cards data from ERB special cards markdown file
-- This migration inserts all special cards from the overpower-erb-specials.md file

-- First, clear any existing placeholder data
DELETE FROM special_cards WHERE id IN ('special_card_type', 'special_special_card');

-- Insert all special cards
ON CONFLICT (id) DO UPDATE SET
  name = EXCLUDED.name,
  character_name = EXCLUDED.character_name,
  universe = EXCLUDED.universe,
  card_effect = EXCLUDED.card_effect,
  image_path = EXCLUDED.image_path,
  alternate_images = EXCLUDED.alternate_images,
  one_per_deck = EXCLUDED.one_per_deck,
  cataclysm = EXCLUDED.cataclysm,
  ambush = EXCLUDED.ambush,
  assist = EXCLUDED.assist;
